settings.custom_css:
.image-slideshow-position--left .flickity-buttons-container {
	top: 50%;
	transform: translateY(-50%);
left: auto;
  right: auto;
}
.slideshow-with-text .flickity-prev-next-button.previous {
	left: -20px;
}
.slideshow-with-text .flickity-prev-next-button.next {
	right: -25px;
}
.image-slideshow-column.three-fifths.medium-down--one-whole.column.image-slideshow-position--left {
  width: 40%;
}
.text-slideshow-column.two-fifths.medium-down--one-whole.column.is-align-center.is-flex-wrap {
  width: 60%;
}
.text-slideshow__slide {
  padding: 0 3em;
}
.text-slideshow__heading.banner__subheading p {
  color: #969696;
}
.flickity-prev-next-button .flickity-button-icon {
  width: 14px;
}
.image-slideshow .flickity-buttons-container {
  width: 100%;
}

.total-balance .content h3 {
	font-size: 22px !important;
	color: #005895;
	margin-top: 20px;
   margin-bottom: 0;
}
.total-balance .content p {
	font-size: 18px;
	font-family: lora-regular;
	margin-bottom: 0 !important;
}

