settings.custome_css:
.omega-3-overview-tab .interest .interest__tabs .tabs__nav .tabs__control {
     font-size: 20px;
}
 @media (min-width: 1200px){
     .omega-3-overview-tab .interest .interest__tabs .tabs__nav .tabs__control {
         width: 370px;
    }
}
 @media (max-width: 1200px){
     .omega-3-overview-tab .interest .interest__tabs .tabs__nav .tabs__control {
         font-size: 16px;
    }
}
 @media (max-width: 767px){
     .omega-3-overview-tab .interest .product-grid{
         flex-direction: column;
         flex-wrap: wrap;
    }
     .omega-3-overview-tab .interest__container .product-grid > .js-product{
         width: calc(100% - 40px);
    }
}
 @media (max-width: 480px){
     .omega-3-overview-tab .qty-selector__dials{
         width: 100%;
    }
     .omega-3-overview-tab .quantity-wrapper.quantity-style--box .quantity-input-control--fill input{
         width: calc(100% - 90px);
    }
     .product-info-column .new_add_tocart_cr {
         max-width: 100%;
    }
}
 @media (min-width: 481px) and (max-width: 767px){
     .interest__container .purchase-details {
         display: flex;
    }
     .quantity-wrapper.quantity-style--box .quantity-input-control {
         width: inherit;
    }
}
