settings.custom_css:
.back_clr{
position: relative;
padding: 40px 0;
}
.back_clr::before {
	width: calc(0% + 100vw);
	position: absolute;
	top: 0;
	left: calc(100% - 98vw);
	bottom: 0;
	content: "";
	background-color: #e8e8e8;
}
.back_clr::after {
	width: calc(100vw - 100%);
	position: absolute;
	top: 0;
	right: calc(100% - 100vw);
	bottom: 0;
	content: "";
	background-color: #e8e8e8;
}
.image-with-text__text-column {
	width: calc(40% - 17.5px);
}
.image-with-text__image-column {width: calc(60% - 17.5px);
}
.image-with-text__text-column {
	padding: 30px 0;
	padding-right: 25px;
}
