settings.custom_css:
.width_bind {
	width: 95%;
	max-width: 1280px;
}
.image-slideshow-position--right .flickity-buttons-container {
	top: 50%;
	transform: translateY(-50%);
left: auto;
z-index: 999;
}
.video_slide  .flickity-prev-next-button.previous {
	left: -30px;
}
.video_slide .flickity-prev-next-button.next {
	right: -70px !important;
}
.image-slideshow-column.three-fifths.medium-down--one-whole.column.image-slideshow-position--right {
  width: 50%;
}
.text-slideshow-column.two-fifths.medium-down--one-whole.column.is-align-center.is-flex-wrap {
  width: 50%;
}
.text-slideshow__slide {
  padding: 0;
}
.text-slideshow__heading.banner__subheading p {
  color: #969696;
}
.flickity-prev-next-button .flickity-button-icon {
  width: 14px;
}
.image-slideshow .flickity-buttons-container {
  width: 100%;
}
.image-element__wrap {
	width: 100%;
	height: auto;
}
a {
	display: block;
	width: 100%;
}
img {
	border-radius: 10px;
}
.text-slideshow-column {
  background: transparent;
}

