settings.custom_css:
.faq-accordion dt button {
	color: #C49159;
	text-transform: uppercase;
}
.faq__column-1{
	background: #F4F2F1;
	vertical-align: middle;
	display: flex;
	align-items: center;
}
.faq{margin:0;}
.padd-left {padding: 20px;}
.faq .image-element__wrap {
	border-radius: 0;
}

.shipping_coll {
	width: 100%;
	position: relative;
	display: flex;
	color: #232020;
	line-height: 1.25;
	padding: 1.5em;
	margin: 0px;
	background: #FFF32A;
}
.shipping_coll::before {
	width: calc(0% + 100vw);
	position: absolute;
	top: 0;
	left: calc(100% - 96vw);
	bottom: 0;
	content: "";
        background: #FFF32A;
}
.shipping_coll::after {
	width: calc(100vw - 100%);
	position: absolute;
	top: 0;
	right: calc(100% - 100vw);
	bottom: 0;
	content: "";
       background: #FFF32A;
}
